import React, {useState} from 'react';

const App = () =>{
const [input, setinput] = useState(6);
const [error, seterror] = useState();

const style = {};
const change = () =>{
    if(input == 6){
        style = {
            backgroundColor:'red',
     
     height:'100px',
     width:'200px',
        }
        seterror('bad password');
    }
    else if(input == 16){
        style = {
            backgroundColor:'orange',
  
  height:'100px',
  width:'200px',
        }
        seterror('weak password');
    }
    else if(input == 24){
        style = {
            backgroundColor:'green',
   
   height:'100px',
   width:'200px',
        }
        seterror('medium password');
    }
    else if(input == 32){
        style = {
            backgroundColor:'darkgreen',
height:'100px',
width:'200px',

        }
        seterror('strong password');
    }
}
    return(
        <>
        <div style={style}>
            <input type="text" />
            <h1>{error}</h1>
        </div>
        <br/>
        <input type="text" value={input} onChange={(e) =>{
            setinput(e.target.value);
        }}/>
        <button onClick={change}
        >Change</button>
          </>
    )
}
export default App;
