import React, {useState} from 'react';

const App = () =>{
    
const [c1, setc1] = useState();
const [c2, setc2] = useState();
const [c3, setc3] = useState();
const [c4, setc4] = useState();

let a = '';
const show = () =>{
    if(c1){
        a += "as";
        console.log(a);
        if(c2){
            a += "hi";
            console.log(a);
            if(c3){
                a += "sh";
                console.log(a);
                if(c4){
                    a += "agrawal";
                    console.log(a);
                }
            }
        }
    }
    else{
        alert('choose any one');
    }
}
    return(
        <>
        <input type="checkbox" value={c1} onChange={(e) =>{
            setc1((e.target.value));
          } }/><span>Uppercase</span>
          <input type="checkbox" value={c2} onChange={(e) =>{
            setc2((e.target.value));
          }} /><span>lowercase</span>
          <input type="checkbox" value={c3} onChange={(e) =>{
            setc3((e.target.value));
          }} /><span>number</span>
          <input type="checkbox" value={c4} onChange={(e) =>{
            setc4((e.target.value));
          }} /><span>symbol</span>
          <br/>
          <button onClick={show}>Click</button>
          <br/>
          <h1>{a}</h1>
          
          </>
    )
}
export default App;
