import React, {useState} from 'react';

const App = () =>{
const [input, setinput] = useState(6);
const [error, seterror] = useState();
const [color, setcolor] = useState('white');

let style = {};
const change = () =>{
    if(input == 6){
        setcolor('red');
        seterror('bad password');
    }
    else if(input == 16){
        setcolor('orange');
        seterror('weak password');
    }
    else if(input == 24){
        setcolor('green');
        seterror('medium password');
    }
    else if(input == 32){
       setcolor('darkgreen');
        seterror('strong password');
    }
}
    return(
        <>
        <div style={{height:'100px', width:'300px',padding:'20px', backgroundColor:`${color}`}}>
            <input type="text" />
            <h1>{error}</h1>
        </div>
        <br/>
        <input type="text" value={input} onChange={(e) =>{
            setinput(e.target.value);
        }}/>
        <button onClick={change}
        >Change</button>
          </>
    )
}
export default App;
